/**
 * @file mPointer.h
 * @brief Test pointer shallow copy in C++
 * @author jfu
*/
#include <iostream>
#include <string.h>
using namespace std;

/**
 * @brief class Person without copy constructor
 * @version v1.0
*/
class Person
{
private:
    char *m_name;
    int m_age;

public:
    Person(char *name, int age)
    {
        m_name = new char[20];
        strcpy(m_name, name);
        m_age = age;
    }
    void setName(char *name)
    {
        strcpy(m_name, name);
    }
    void showInfo()
    {
        cout << "My name is: " << m_name << " and my age is " << m_age << endl;
    }
};

int TestPointer();

/**
 * @file mPointer.cpp
 * @brief Test class Person in file mPointer.h
 * @author jfu
*/
#include "mPointer.h"

/**
 * @brief Test shallow copy
*/
int TestPointer()
{
    Person p("Tom", 20);
    p.showInfo();

    Person q = p;
    q.showInfo();
    q.setName("Jack");

    p.showInfo();
    q.showInfo();
    return 0;
}

/**
 * @file main.cpp
 * @brief Main file for learning C++. This file using functions, variables in other files.
 * @author jfu
*/
#include "mPointer.h"

int main()
{
    TestPointer();
    return 0;
}
